function ordena_por_nombre_ascd($persona1, $persona2)    
{
    return strcmp($persona1['nombre'], $persona2['nombre']);
}

function ordena_por_apellidos_ascd($persona1, $persona2)    
{
    return strcmp($persona1['apellidos'], $persona2['apellidos']);
}

function ordena_por_fecha_nacimiento_ascd($persona1, $persona2)    
{
    return strcmp($persona1['fecha_nacimiento'], $persona2['fecha_nacimiento']);
} 

//2parte

function ordena_por_nombre_desc($persona1, $persona2)    
{
    return ordena_por_nombre_ascd($persona1, $persona2)*(-1);
}

function ordena_por_apellidos_desc($persona1, $persona2)    
{
    return ordena_por_apellidos_ascd($persona1, $persona2)*(-1);
}

function ordena_por_fecha_nacimiento_desc($persona1, $persona2)    
{
    return ordena_por_fecha_nacimiento_ascd($persona1, $persona2)*(-1);
}

